import React from 'react'

const Login = () => {
    const navigate = useNavigate();
const [email,setEmail]=useState("")
const [password,setPassword]=useState("") 
const  token  = localStorage.getItem('token') 
const[islogindeclined,Setislogindeclined]=useState(false)





const submitHandler=async(e)=>{
    e.preventDefault()
    const loginData= {
        email: email,
        password: password
    }

    await axios.post('http://localhost:4000/login',loginData).then((response)=>{   
        console.log('response from loin iss   '); 
        console.log('response.data,declined is     ',response.data.declined);
        if(response.data.declined){
            
            Setislogindeclined(true)

        }else{


            
            console.log(response.data.token);
    
            const {token}=response.data.token;
            localStorage.setItem( "token",response.data.token);
            // const {uId}=response.data.uid;
            localStorage.setItem( "uId",response.data.uid);
            
    
    
    
            setEmail("")
            setPassword("")
    
    
            
            navigate('/')
        }



    }).catch((err => {
        console.log(err);
    }))
}








  return (
    <div className="relative flex flex-col justify-center min-h-screen overflow-hidden bg-black ">
    <div className=" w-full p-6 m-auto bg-black rounded-md shadow-2xl shadow-violet-600/100  lg:max-w-xl">
        <h1 className="text-3xl font-semibold text-center text-violet-900 md:text  uppercase ">
           Sign in
        </h1>
        <form className="mt-6" onSubmit={submitHandler}>
            <div className="mb-2">
                <label
                    for="email"
                    className="block text-sm font-semibold text-white"
                >
                    Email
                </label>
                <input type="email" onChange={(e)=>{  setEmail(e.target.value) }}
                    value={email}
                    className="block w-full px-4 py-2 mt-2 text-black bg-white border rounded-md focus:border-purple-400 focus:ring-purple-300 focus:outline-none focus:ring focus:ring-opacity-40"
                />
            </div>
            <div className="mb-2">
                <label
                    for="password"
                    className="block text-sm font-semibold text-white"
                >
                    Password
                </label>
                <input
                    type="password"
                    onChange={(e)=>{
                        setPassword(e.target.value)
                    }}
                    value={password}
                    className="block w-full px-4 py-2 mt-2 text-black bg-white border rounded-md focus:border-purple-400 focus:ring-purple-300 focus:outline-none focus:ring focus:ring-opacity-40"
                />
            </div>
            {/* <a
                href="#"
                className="text-xs text-orange-600 hover:underline"
            >
                Forget Password?
            </a> */}
           {islogindeclined && <span className='text-red-600'>invalid credentials</span>}
            <div className="mt-6">
                <button type='submit' className="w-full px-4 py-2 tracking-wide text-white transition-colors duration-200 transform bg-violet-900 rounded-md hover:bg-violet-600 focus:outline-none focus:bg-purple-600">
                    Login
                </button>
            </div>
        </form>

        <p className="mt-8 text-xs font-light text-center text-white">
            {" "}
            Don't have an account?{" "}
            <a
                onClick={() =>{
                    navigate('/signup');
                }}
                className="font-medium text-violet-900 hover:underline" style={{cursor: "pointer"}}
            >
                Sign up
            </a>
        </p>
    </div>
</div>
  )
}

export default Login
