import React from 'react'
import {useNavigate } from 'react-router-dom';
import axios from '../config/config'
import {useForm} from 'react-hook-form';




const Signup = () => {
const navigate = useNavigate();
const { register,unregister, handleSubmit, formState: { errors },reset } = useForm();

const signUpHandler=async(data,e)=>{
    e.preventDefault()
    


    const signUpData={
        uname:data.name,
        email:data.mail,
        phone:data.phone,
        password:data.password
    }
    console.log('data details iss  ',data);
    console.log('crista sewwyyy');
    console.log('object iss ',signUpData);

    await axios.post('http://localhost:4000/signup',signUpData).then((Response)=>{
        console.log('data send');
        navigate('/login');
    }).catch((err)=>{
        console.log('errroorrrr');
        console.log(err);
    })

  
    reset();

}


  return (
    <div>

<div className="relative flex flex-col justify-center min-h-screen overflow-hidden bg-white">
            <div className=" w-full p-6 m-auto bg-white rounded-md shadow-xl shadow-violet-600/100  lg:max-w-xl">
                <h1 className="text-3xl font-semibold text-center text-violet-900  md:text  uppercase ">
                   Sign up
                </h1>
                <form className="mt-6" onSubmit={handleSubmit(signUpHandler)}>
                    <div className='flex'>
                    <div className="mb-2 mx-1">
                        <label
                            for="email"
                            className="block text-sm font-semibold text-white"
                        >
                            Username
                        </label>
                        <input
                            type="text"
                            placeholder='first name'
                            // onChange={(e) => { setUsername(e.target.value)}}
                            // value={username}
                            className="block w-full px-4 py-2 mt-2 text-black bg-white border rounded-md focus:border-purple-400 focus:ring-purple-300 focus:outline-none focus:ring focus:ring-opacity-40"
                            {...register("firstname", {
                                required: 'firstname is required' ,
                                pattern:{
                                    value:/^[A-Za-z]+$/,
                                    message:'Should only contain characters '
                                
                            }
                              })}
                        />
                        {errors.firstname&&(<small className='text-red-700'>{errors.firstname.message}</small>)}
                    </div>
                    <div className="mb-2 mx-1">
                        <label
                            for="email"
                            className="block text-sm font-semibold text-white"
                        >
                            Username
                        </label>
                        <input
                            type="text"
                            placeholder='middle name'
                            name='middlename'
                            // onChange={(e) => { setUsername(e.target.value)}}
                            // value={username}
                            className="block w-full px-4 py-2 mt-2 text-black bg-white border rounded-md focus:border-purple-400 focus:ring-purple-300 focus:outline-none focus:ring focus:ring-opacity-40"
                            // {...unregister("middlename")}
                        />
                        {/* {errors.middlename&&(<small className='text-red-700'>{errors.middlename.message}</small>)} */}
                    </div>
                    <div className="mb-2 mx-1">
                        <label
                            for="email"
                            className="block text-sm font-semibold text-white"
                        >
                            Username
                        </label>
                        <input
                            type="text"
                            placeholder='last name'
                            // onChange={(e) => { setUsername(e.target.value)}}
                            // value={username}
                            className="block w-full px-4 py-2 mt-2 text-black bg-white border rounded-md focus:border-purple-400 focus:ring-purple-300 focus:outline-none focus:ring focus:ring-opacity-40"
                            {...register("lastname", {
                                required: 'Name is required' ,
                                pattern:{
                                    value:/^[A-Za-z]+$/,
                                    message:'Should only contain characters '
                                
                            }
                              })}
                        />
                        {errors.lastname&&(<small className='text-red-700'>{errors.lastname.message}</small>)}
                    </div>
                    </div>



                    <div className='flex justify-around'>
                 
                    <div className="mb-2">
                        <label
                            for="email"
                            className="block text-sm font-semibold text-white"
                        >
                            Email
                        </label>
                        <input
                            type="date"
                            name='date'
                            className="block w-full px-4 py-2 mt-2 text-black bg-white border rounded-md focus:border-purple-400 focus:ring-purple-300 focus:outline-none focus:ring focus:ring-opacity-40"
                            // {...register("mail", {
                            //     required: 'Email is required' , pattern:{
                            //         value:/^\w+([-+.']\w+)*@\w+([-.]\w+)*\.\w+([-.]\w+)*$/,
                            //         message: 'Enter valid email'
                            //     }
                            //   })}
                        />
                                                {/* {errors.mail&&(<small className='text-red-700'>{errors.mail.message}</small>)} */}



                    </div>
                    <div className="mb-2">
                        <label
                            for="email"
                            className="block text-sm font-semibold text-white"
                        >
                            Phone
                        </label>
                        <input
                            type="number"
                            placeholder='phone number'
                            // onChange={(e) => { setPhone(e.target.value)}}
                            // value={phone}
                            className="block w-full px-4 py-2 mt-2 text-black bg-white border rounded-md focus:border-purple-400 focus:ring-purple-300 focus:outline-none focus:ring focus:ring-opacity-40"
                            {...register("phone", {
                                required: 'Phone number is required' ,
                                    pattern:{
                                        value:/^[6-9]\d{9}$/,
                                        message:'invalid phone number'
                                    
                                }
                              })}
                        />
                                                {errors.phone&&(<small className='text-red-700'>{errors.phone.message}</small>)}


                    </div>
                    </div>




           <div className='flex justify-around'>
           <div className="mb-2">
                        <label
                            for="email"
                            className="block text-sm font-semibold text-white"
                        >
                            Email
                        </label>
                        <input
                            type="mail"
                            placeholder='e-mail'
                            className="block w-full px-4 py-2 mt-2 text-black bg-white border rounded-md focus:border-purple-400 focus:ring-purple-300 focus:outline-none focus:ring focus:ring-opacity-40"
                            {...register("mail", {
                                required: 'Email is required' , pattern:{
                                    value:/^\w+([-+.']\w+)*@\w+([-.]\w+)*\.\w+([-.]\w+)*$/,
                                    message: 'Enter valid email'
                                }
                              })}
                        />
                                                {errors.mail&&(<small className='text-red-700'>{errors.mail.message}</small>)}



                    </div>
                    <div className="mb-2">
                        <label
                            for="password"
                            className="block text-sm font-semibold text-white"
                        >
                            Password
                        </label>
                        <input
                            type="password"
                            placeholder='password'
                            // onChange={(e) => { setPassword(e.target.value)}}
                            // value={password}
                            className="block w-full px-4 py-2 mt-2 text-black bg-white border rounded-md focus:border-purple-400 focus:ring-purple-300 focus:outline-none focus:ring focus:ring-opacity-40"
                            {...register("password", {
                                required: 'Password  is required' ,
                                pattern:{
                                    value: /^(?=.*[0-9])(?=.*[!@#$%^&*])[a-zA-Z0-9!@#$%^&*]{6,16}$/,
                                    message:'should have each character,digit & specialcharacter'
                                
                            }
                               
                              })}
                        />
                                                {errors.password&&(<small className='text-red-700'>{errors.password.message}</small>)}


                    </div>
                    </div>


                    <div className='flex justify-around'>
                    <div className="mb-2 mx-1">
                        <label
                            for="email"
                            className="block text-sm font-semibold text-white"
                        >
                            Username
                        </label>
                        <input
                            type="text"
                            placeholder='Company'
                            name='Company'
                            // onChange={(e) => { setUsername(e.target.value)}}
                            // value={username}
