import React from 'react'

const Signup = () => {
  return (
    <div>

<div className="relative flex flex-col justify-center min-h-screen overflow-hidden bg-black">
            <div className=" w-full p-6 m-auto bg-black rounded-md shadow-xl shadow-violet-600/100  lg:max-w-xl">
                <h1 className="text-3xl font-semibold text-center text-violet-900  md:text  uppercase ">
                   Sign up
                </h1>
                <form className="mt-6" onSubmit={handleSubmit(signUpHandler)}>
                    <div className="mb-2">
                        <label
                            for="email"
                            className="block text-sm font-semibold text-white"
                        >
                            Username
                        </label>
                        <input
                            type="text"
                            // onChange={(e) => { setUsername(e.target.value)}}
                            // value={username}
                            className="block w-full px-4 py-2 mt-2 text-black bg-white border rounded-md focus:border-purple-400 focus:ring-purple-300 focus:outline-none focus:ring focus:ring-opacity-40"
                            {...register("name", {
                                required: 'Name is required' ,
                                pattern:{
                                    value:/^[A-Za-z]+$/,
                                    message:'Should only contain characters '
                                
                            }
                              })}
                        />
                        {errors.name&&(<small className='text-red-700'>{errors.name.message}</small>)}
                    </div>
                    <div className="mb-2">
                        <label
                            for="email"
                            className="block text-sm font-semibold text-white"
                        >
                            Email
                        </label>
                        <input
                            type="text"
                            // onChange={(e) => { setEmail(e.target.value)}}
                            // value={email}
                            className="block w-full px-4 py-2 mt-2 text-black bg-white border rounded-md focus:border-purple-400 focus:ring-purple-300 focus:outline-none focus:ring focus:ring-opacity-40"
                            {...register("mail", {
                                required: 'Email is required' , pattern:{
                                    value:/^\w+([-+.']\w+)*@\w+([-.]\w+)*\.\w+([-.]\w+)*$/,
                                    message: 'Enter valid email'
                                }
                              })}
                        />
                                                {errors.mail&&(<small className='text-red-700'>{errors.mail.message}</small>)}



                    </div>
                    <div className="mb-2">
                        <label
                            for="email"
                            className="block text-sm font-semibold text-white"
                        >
                            Phone
                        </label>
                        <input
                            type="number"
                            // onChange={(e) => { setPhone(e.target.value)}}
                            // value={phone}
                            className="block w-full px-4 py-2 mt-2 text-black bg-white border rounded-md focus:border-purple-400 focus:ring-purple-300 focus:outline-none focus:ring focus:ring-opacity-40"
                            {...register("phone", {
                                required: 'Phone number is required' ,
                                    pattern:{
                                        value:/^[6-9]\d{9}$/,
                                        message:'invalid phone number'
                                    
                                }
                              })}
                        />
                                                {errors.phone&&(<small className='text-red-700'>{errors.phone.message}</small>)}


                    </div>
                    <div className="mb-2">
                        <label
                            for="password"
                            className="block text-sm font-semibold text-white"
                        >
                            Password
                        </label>
                        <input
                            type="password"
                            // onChange={(e) => { setPassword(e.target.value)}}
                            // value={password}
                            className="block w-full px-4 py-2 mt-2 text-black bg-white border rounded-md focus:border-purple-400 focus:ring-purple-300 focus:outline-none focus:ring focus:ring-opacity-40"
                            {...register("password", {
                                required: 'Password  is required' ,
                                pattern:{
                                    value: /^(?=.*[0-9])(?=.*[!@#$%^&*])[a-zA-Z0-9!@#$%^&*]{6,16}$/,
                                    message:'should have each character,digit & specialcharacter'
                                
                            }
                               
                              })}
                        />
                                                {errors.password&&(<small className='text-red-700'>{errors.password.message}</small>)}


                    </div>
                    {/* <a
                        href="#"
                        className="text-xs text-orange-600 hover:underline"
                    >
                        Forget Password?
                    </a> */}
                    <div className="mt-6">
                        <button type='submit' className="w-full px-4 py-2 tracking-wide text-white transition-colors duration-200 transform bg-violet-900  rounded-md hover:bg-violet-600  focus:outline-none focus:bg-purple-600">
                            Signup
                        </button>
                    </div>
                </form>

                <p className="mt-8 text-xs font-light text-center text-white">
                    {" "}
                    Already have an account?{" "}
                    <a 
                          onClick={() =>{
                            navigate('/login');
                        }}
                        className="font-medium text-violet-900  hover:underline" style={{cursor: "pointer"}}
                    >
                        Sign in
                    </a>
                </p>
            </div>
        </div>


    </div>
  )
}

export default Signup
